mapper.select:
SELECT w.pid,w.cid,w.measure_name,w.blowdown_outlet ,p.monitor_name blowDownOutLetName ,z.value wastewaterCategoryName ,w.model,w.process,w.wastewater_category ,w.removal_of_material,w.total_investment ,w.delivery_date,w.processing_capacity,w.actual_processing,w.running_state ,w.year_throughput,w.year_operating_cost,w.remarks ,w.createdate,w.updatedate,w.deletedate ,w.single_design_capability,w.day_runtime,w.governance_limit,w.acceptance_opinion, w.handle_other_unitWater,w.airtight_situation, w.handle_whereabouts, w.annualo_perating_hours FROM cominfo_measure_wastewater w LEFT JOIN online_monitor_portinfo p ON p.pid=w.blowdown_outlet LEFT JOIN sys_dict z ON z.key=w.wastewater_category AND z.type='wastewater_category_type' WHERE 1=1
AND w.cid=#{cid}
AND w.blowdown_outlet=#{mid}
order by w.delivery_date DESC
limit #{offset}, #{limit}

SELECT count(1)
FROM cominfo_measure_wastewater w
LEFT JOIN online_monitor_portinfo p ON p.pid=w.blowdown_outlet
WHERE 1=1

    AND w.cid=#{cid}


    AND w.blowdown_outlet=#{mid}
