SELECT w.pid,w.cid,w.measure_name,w.blowdown_outlet,p.monitor_name blowDownOutLetName,w.model,w.process,w.total_investment ,w.delivery_date,w.processing_capacity,w.actual_processing,w.running_state,w.year_throughput,w.year_operating_cost,w.remarks ,w.createdate,w.updatedate,w.deletedate ,w.single_design_capability,w.day_runtime,w.governance_limit,w.acceptance_opinion ,w.annual_operating_hours ,w.sulfur_remove_device_code ,w.sulfur_emove_tech ,w.sulfur_remove_efficiency ,w.sulfur_remove_chemical ,w.sulfur_remove_agent ,w.is_ow_burn_remove_sulfur ,w.nitre_remove_device_code ,w.nitre_Remove_tech ,w.nitre_remove_efficiency ,w.nitre_remove_chemical ,w.nitre_remove_agent ,w.isLow_burn_removeNitre ,w.dust_remove_device_code ,w.dust_remove_tech ,w.dust_remove_efficiency FROM cominfo_measure_wastegas w LEFT JOIN online_monitor_portinfo p ON p.pid=w.blowdown_outlet WHERE 1=1
AND w.cid=#{cid}
AND w.blowdown_outlet=#{mid}
order by w.delivery_date DESC
limit #{offset}, #{limit}

